pluginOptions.query:
{
  site {
    siteMetadata {
      title
      description
      siteUrl
      site_url: siteUrl
    }
  }
}

feeds.query:
{
  allMarkdownRemark(
    limit: 1000,
    sort: { order: DESC, fields: [frontmatter___date] },
  ) {
    edges {
      node {
        excerpt
        html
        frontmatter {
          title
          date
          path
        }
      }
    }
  }
}
